import React, { useState ,useEffect} from "react";
import logo from "./logo.svg";
import "./App.css";


var colorArray = ['#FF6633', '#FFB399', '#FF33FF', '#FFFF99', '#00B3E6', 
		  '#E6B333', '#3366E6', '#999966', '#99FF99', '#B34D4D',
		  '#80B300', '#809900', '#E6B3B3', '#6680B3', '#66991A', 
		  '#FF99E6', '#CCFF1A', '#FF1A66', '#E6331A', '#33FFCC',
		  '#66994D', '#B366CC', '#4D8000', '#B33300', '#CC80CC', 
		  '#66664D', '#991AFF', '#E666FF', '#4DB3FF', '#1AB399',
		  '#E666B3', '#33991A', '#CC9999', '#B3B31A', '#00E680', 
		  '#4D8066', '#809980', '#E6FF80', '#1AFF33', '#999933',
		  '#FF3380', '#CCCC00', '#66E64D', '#4D80CC', '#9900B3', 
		  '#E64D66', '#4DB380', '#FF4D4D', '#99E6E6', '#6666FF'];

function App() {






  const [text,setText] = useState("");
  const [author,setAuthor] = useState("");
  const [color,setColor] = useState("#00B1E1")
  const style = {
    backgroundColor: color,
    height:'100%',
    width:'100%'

  };

  function getAPIResult(){

    var result= "";
    fetch("https://api.quotable.io/random").then((res)=>res.json()).then((res)=>{

      var item = colorArray[Math.floor(Math.random() * colorArray.length)]

      setText(res.content)
      setAuthor(res.author)
      setColor(item)
    })

  }

  useEffect(() => {
    // Update the document title using the browser API
    if(text!=""){

    }else{
      getAPIResult();
    }



  });
  const bgcolor = {
    backgroundColor: {color}
  };

  console.log(text)

  return (
    <div className="container-fluid" style={style}>
      <div className="row" style={{ height: "100%", backgroundColor: "" }}>
        <div className="col-12" style={{ textAlign: "center"}}>
        <Quetes name={text} author={author} getAPIResult={getAPIResult} color={color}/>
        </div>
      </div>


    </div>
  );
}


const Quetes = ({name,author,getAPIResult,color})=>{

  const box_style = {
    width:"40%",
    height:"40%",
    backgroundColor:"",
    margin:"0 auto",
    position:'relative',
    top:"30%",
    border:"1px solid #1C6EA4",
    borderRadius:"3px",
    backgroundColor:"#fff"
  }

  return(
    <div className="" style={box_style}>
      <div style={{height:"70%",padding:"10px"}}>
      <i className="fa fa-quote-left"></i>
      <blockquote style={{fontWeight:'500',fontSize:"1.5em",color:color}}>
      {name}
      </blockquote>
      <p style={{float:"right",margin:"10px"}}>
        - {author}
      </p>
      </div>
      <div style={{height:"",position:"relative",float:"right"}}>
      <button style={{margin:"10px",display:"inline",backgroundColor:color,border:"none",color:"white",borderRadius:"3px",height:"40px",width:"100px"}} onClick={()=>{
        getAPIResult();
      }}>Next</button>
      </div>

    </div>
  )
}

export default App;
